import React, { useState, useEffect } from 'react'
import Stars from '../Stars/Stars'

import "./modal.css"

const Modal = ({active, setActive}) => {
    const [viewOptions, setViewOptions] = useState('')
    const [disable, setDisable] = useState(false)
    const skillRef = React.useRef()
    const [selectedKey, setSelectedKey] = useState('')
    
    function findOption(select) {
        const option = select.querySelector(`option[value="${select.value}"]`)
        if(option === 1){
            setDisable(false)
        }
        else{
            setDisable(true)
        }
     }

    const data = {
        "Базы данных": [ "PostgreSQL", "MySQL"],
        "Клиентский веб" : [ "Vue.js", "jQuery"],
        "Серверная часть" : [ "PHP", "Laravel", "Windows Forms"],
        "Десктопная разработка" : [ "C#", "WPF", "Windows Forms"],
        "Мобильная разработка" : [ "Cordova", "Xamarin"],
        "Компьютерное зрение/машинное обучение" : [ "C++", "OpenCV", "Python", "TenserFlow"]
    }
    
  return (
    <div className={active ? "modal active" : "modal"}>
        <div className="modal__content">
        <a onClick={() => setActive(false)}><img src="https://img.icons8.com/ios-glyphs/30/000000/delete-sign.png" /></a>
            <h1>Оцени свои навыки</h1>
            <form action="">
                <div className="modal_select">
                    <h3>Выбрать навык</h3>
                    {/* Skills */}
                    <div className="select-wrapper">
                        <select className='form_select' ref={skillRef}>
                            {
                                Object.keys(data).map((value, index) => <option value={index} key={index}>{value}</option>)
                            }
                        </select>
                    </div>
                    <h3 className='lang_h3'>Выбрать язык</h3>
                    {/* Languages */}
                    <div className="select-wrapper">
                        <select onchange={() => findOption(this)} className='form_select'>
                            { data[Object.keys(data)[selectedKey]].map((value, index) => <option value={index} key={index}>{value}</option>) }
                        </select>
                    </div>
                    <div className="stars">
                        <Stars />
                    </div>
                </div>
                <div className="modal_form">
                    <div className={disable ? "form-button_disabled" : "form-button"}>
                        <button disabled={disable} type="submit">Отправить</button>
                    </div>
                </div>
            </form>
        </div>
    </div>
  )
}

export default Modal
