import React, { useEffect } from 'react'
import { useState } from "react";

import './input.css'

const Input = (props) => {
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();
  const [emailDirty, setEmailDirty] = useState(false);
  const [passwordDirty, setPasswordDirty] = useState(false);
  const [emailError, setEmailError] = useState("Email - не может быть пустым");
  const [passwordError, setPasswordError] = useState('Пароль - не может быть пустым');


  const emailHandler = (e) => {
    setEmail(e.target.value)
    const re = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    if(!re.text(String(e.target.value).toLowerCase())){
      setEmailError("Email некорректный!")
    }
    else{
      setEmailError('')
    }
  }

  const blurHandler = (e) =>{
    switch(e.target.name){
      case 'email':
        setEmailDirty(true)
        break
      case 'password':
        setPasswordDirty(true)
        break
    }
  }
  return (
    <div className="input_form">
      {(emailDirty && emailError) && <div style={{color: 'red'}}>{emailError}</div>}
      <label>
        <input value={email} onChange={e => emailHandler(e)} onBlur={e => blurHandler(e)} className="form_input" name="email" type="text" placeholder="Логин"></input>
      </label>

      {(passwordDirty && passwordError) && <div style={{color: 'red'}}>{passwordError}</div>}
      <label>
        <input value={password} onBlur={e => blurHandler(e)} className="form_input" name="password" type="password" placeholder="Пароль"></input>
      </label>
    </div>
    
    
  )
}
Input.defaultProps = { type: "text", placeHolder: ""};

export default Input
