import React, { useEffect, useState } from "react";
import {BrowserRouter as Router, Route, Routes, Link} from 'react-router-dom'
import { Helmet } from 'react-helmet';

import './App.css';
import Vector from './components/Vector/Vector.jsx';
import Home from './Pages/Home';
import Input from './components/Input/Input';




function App() {

  const [formValid, setFormValid] = useState(false)
  useEffect(() => {
    if(Input.emailError || Input.passwordError){
      setFormValid(false)
    }
    else{
      setFormValid(true)
    }
  }, [Input.emailError, Input.passwordError])

  return(
    <div>
      <Helmet>
        <title>Вход</title>
      </Helmet>
      <div className="link__container">
        <Link className="link" to="/home">Главная</Link>
      </div>
      <form>
        <div className="form">
          <h1>Вход</h1>
          <Input/>
          <div className="form-button">
            <button disabled="true" type="submit">Войти</button>
          </div>
        </div>
      </form>
    </div>
  )
}

function Routering () {


  return(
    <div className="App">
      <Router>
        <Routes>
          <Route exact path="/home" element={<Home />}/>
          <Route exact path="/" element={<App />} />
        </Routes>
      </Router>
      <Vector />
    </div>
  )
}

export default Routering;
