import React from 'react'
import { useState } from "react";

import './input.css'

const Input = (props) => {
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();
  const [emailDirty, setEmailDirty] = useState(false);
  const [passwordDirty, setPasswordDirty] = useState(false);
  const [emailError, setEmailError] = useState("'Email - не может быть пустым'");
  const [passwordError, setPasswordError] = useState('Пароль - не может быть пустым');

  const blurHandler = (e) =>{
    switch(e.target.name){
      case 'email':
        setEmailDirty(true)
        break
      case 'password':
        setPasswordDirty(true)
        break
    }
  }
  return (
    <div className="input_form">
      {(emailDirty && emailError) && <div style={{color: 'red'}}>{emailError}</div>}
      <label>
        <input value={email} onBlur={e => blurHandler(e)} className="form_input" name="login" type="text" placeholder={props.placeHolder}></input>
      </label>

      {(passwordDirty && passwordError) && <div style={{color: 'red'}}>{passwordError}</div>}
      <label>
        <input value={password} onBlur={e => blurHandler(e)} className="form_input" name="login" type="password" placeholder={props.placeHolderPass}></input>
      </label>
    </div>
    
    
  )
}
Input.defaultProps = { type: "text", placeHolder: ""};

export default Input
