import React from "react";
import {BrowserRouter as Router, Route, Routes, Link} from 'react-router-dom'
import { Helmet } from 'react-helmet';
import { useState } from "react";

import './App.css';
import Vector from './components/Vector/Vector.jsx';
import Home from './Pages/Home';
import Input from './components/Input/Input';


function App() {
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();
  const [emailDirty, setEmailDirty] = useState(false);
  const [passwordDirty, setPasswordDirty] = useState(false);
  const [emailError, setEmailError] = useState('Email - не может быть пустым');
  const [passwordError, setPasswordError] = useState('Пароль - не может быть пустым');

  const blurHandler = (e) =>{
    switch(e.target.name){
      case 'email':
        setEmailDirty(true)
        break
      case 'password':
        setPasswordDirty(true)
        break
    }
  }
  return(
    <div>
      <Helmet>
        <title>Вход</title>
      </Helmet>
      <div className="link__container">
        <Link className="link" to="/home">Главная</Link>
      </div>
      <form>
        <div className="form">
          <h1>Вход</h1>
          {(emailDirty && emailError) && <div style={{color: 'red'}}>{emailError}</div>}
          <Input value={email} onBlur={e => blurHandler(e)} mainCN="input_form" cN='form_input' name="email" type="text" placeHolder="Логин"/>
          {(passwordDirty && passwordError) && <div style={{color: 'red'}}>{passwordError}</div>}
          <Input  mainCN="input_form" cN='form_input' name="password" type="password" placeHolder="Пароль"/>
          <div className="form-button">
            <button type="submit">Войти</button>
          </div>
        </div>
      </form>
    </div>
  )
}

function Routering () {


  return(
    <div className="App">
      <Router>
        <Routes>
          <Route exact path="/home" element={<Home />}/>
          <Route exact path="/" element={<App />} />
        </Routes>
      </Router>
      <Vector />
    </div>
  )
}

export default Routering;
